import ControleEditora from "./controle/ControleEditora";
import ControleLivro from "./controle/ControleLivros";
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const controleLivro = new ControleLivro();
const controleEditora = new ControleEditora();

function LivroDados() {
    const [opcoes, setOpcoes] = useState([]);

  useEffect(() => {
    // Ao montar o componente, obter as opções das editoras
    const editoras = controleEditora.getEditoras();

    // Mapear as editoras conforme necessário para o formato desejado
    const opcoesMapeadas = editoras.map(editora => ({
      value: editora.codEditora,
      text: editora.nome
    }));

    // Atualizar o estado com as opções mapeadas
    setOpcoes(opcoesMapeadas);
  }, []);

  return (
    <div>
      {/* Aqui você pode utilizar as opções, por exemplo, em um menu suspenso (select) */}
      <select>
        {opcoes.map(opcao => (
          <option key={opcao.value} value={opcao.value}>
            {opcao.text}
          </option>
        ))}
      </select>
      {/* Outros componentes ou formulários para os dados do livro */}
    </div>
  );
}


export default LivroDados;
